INSERT INTO `tb_organization` VALUES (166, 58362, '444,443', '{\"1\":{\"1\":6,\"2\":27,\"3\":\"444,443\"}}', '上海好姑姑托育园', 'shhggtyy', '[\"http://jsu173.oss-cn-shanghai.aliyuncs.com/upload/15791720185e2040b28c0b96.00969300.jpeg\", \"http://jsu173.oss-cn-shanghai.aliyuncs.com/upload/15791720195e2040b302e6b0.33645310.jpeg\"]', 'http://jsu173.oss-cn-shanghai.aliyuncs.com/upload/15791720185e2040b28c0b96.00969300.jpeg', 'http://jsu173.oss-cn-shanghai.aliyuncs.com/upload/15791720185e2040b22cfef2.98488794.jpeg', '<div class=\"cont-part\"><div class=\"cont-title\"><div class=\"cont-editor\"><p>好姑姑托育园是上海连锁直营的托育品牌，致力于为0-3岁的宝宝提供规范的、高品质的全日托服务。好姑姑托育园拥有一支有经验、富有爱心的优秀教学团队，尊重1-3岁幼儿年龄特点、给予幼儿科学养护。以4S教育理念(自理、自律、自信、自理能力培养）为核心，围绕幼儿的感统发展、动作协调、语言能力发展、社交情感发展等方面，通过生活自理能力引导教育、游戏活动、运动活动等形式，科学、规范地开展一日活动，保障幼儿在园安全、开心、充实地度过每一天。</p><p>好姑姑托育园致力于为孩子们营造一个温馨、安全的成长环境，是宝宝身边的守护型托育园。</p></div></div></div>', '徐汇区天钥桥南路', 121.461793, 31.160228, '[{\"title\":\"\\u9f99\\u5357\\u6258\\u80b2\\u56ed\",\"address\":\"\\u5f90\\u6c47\\u533a\\u5929\\u94a5\\u6865\\u5357\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u51e4\\u57ce\\u6258\\u80b2\\u56ed\",\"address\":\"\\u6768\\u6d66\\u533a\\u5468\\u5bb6\\u5634\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u5927\\u534e\\u9526\\u7ee3\\u534e\\u57ce\\u6258\",\"address\":\"\\u6d66\\u4e1c\\u534e\\u9e4f\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u7eff\\u5730\\u6c47\\u521b\\u6258\\u80b2\\u56ed\",\"address\":\"\\u56fd\\u5b9a\\u4e1c\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u6885\\u9647\\u6258\\u80b2\\u56ed\",\"address\":\"\\u51cc\\u4e91\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u5927\\u5b81\\u91d1\\u8302\\u5e9c\\u6258\\u80b2\",\"address\":\"\\u5f6d\\u6c5f\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u4f73\\u4fe1\\u5f90\\u6c47\\u6258\\u80b2\\u56ed\",\"address\":\"\\u6f15\\u4e1c\\u652f\\u8def\"},{\"title\":\"\\u597d\\u59d1\\u59d1\\u73b2\\u73d1\\u574a\\u6258\\u80b2\\u56ed\",\"address\":\"\\u4e0a\\u6d77\\u5e02\\u5609\\u5b9a\\u533a\\u5eb7\\u5e74\\u8def\"},{\"title\":\"\\u597d\\